import React from "react";
import uuid from "uuid/v1";
import formatDate from "./utils";
import faceImage from "../assets/Images/face.png";

export default function comments(props) {
  const commentList = props.comments.map((com, index) => {
    return (
      <div key={uuid()} className="comment">
        <div className="comment__left">
          <img src={faceImage} alt="user-face" />
        </div>
        <div className="comment__right">
          <div className="comment__right-top">
            <p className="comment__right-top-name">
              {com[com.length - 1 - index].name}
            </p>
            <p className="comment__right-top-date">
              {formatDate(com[com.length - 1 - index].timestamp)}
            </p>
          </div>
          <div className="comment__paragraph">
            <p className="comment__paragraph-text">
              {com[com.length - 1 - index].comment}
            </p>
          </div>
        </div>
      </div>
    );
  });

  return <>{commentList}</>;
}
