import React from "react";
import VideoControls from "./videoControls";
const $key = "?api_key=Daniel";

export default class Video extends React.Component {
  source = props.mainVideo.video + $key;
  ref = React.createRef();

  render() {
    return (
      <>
        <div className="video">
          <video
            src={source}
            ref={ref}
            className="video__playing"
            poster={props.mainVideo.image}
          ></video>
        </div>
        <VideoControls video={ref} />
      </>
    );
  }
}
