import React from "react";
import fullScreen from "../assets/Icons/SVG/Icon-fullscreen.svg";
import volume from "../assets/Icons/SVG/Icon-volume.svg";
import playIcon from "../assets/Icons/SVG/Icon-play.svg";
//import pauseIcon from "../assets/Icons/SVG/Icon-pause.svg";

export default function videoContorls(props) {
  findElement = () => {
    const node = ReactDOM.findDOMNode(this);
    return node;
  };
  let video = props.dom;
  console.log(video);

  const playPause = () => {
    video.play();
  };
  return (
    <>
      <div className="control">
        <div className="control__playPause">
          <button
            onClick={playPause}
            className="control__playPause-button"
            alt=""
          >
            <img src={playIcon}></img>
          </button>
        </div>
        <div className="control__progressBar">
          <span className="control__progress"></span>
          <span className="control__time">0:00/0:00</span>
        </div>
        <div className="control__volumeScreen">
          <button className="control__volume">
            <img src={fullScreen} alt="" />
          </button>
          <button className="control__fullScreen">
            <img src={volume} alt="" />
          </button>
        </div>
      </div>
    </>
  );
}
