import React, { Component } from "react";
import Header from "./components/header";
import Comments from "./components/comments";
import Conversation from "./components/conversation";
import Video from "./components/video";
import VideoInfo from "./components/videoInfo";
import VideoList from "./components/videoList";
import VideoControls from "./components/videoControls";
import Upload from "./components/upload";
import axios from "axios";
import "./styles/main.css";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

const $url = "https://project-2-api.herokuapp.com/videos/";
const $key = "?api_key=Daniel";
const $homeKey = "1af0jruup5gu";
const fs = require("fs");
const videoLists = __dirname + "/models/videoList.json";
const videoss = require(videoLists);

class App extends Component {
  state = {
    videoList: [],
    mainVideo: {},
    commentCount: 0,
    comments: [],
    mainVideoId: $homeKey
  };

  componentDidMount() {
    axios.get($url + $key).then(response => {
      fs.writeFileSync(
        videoLists,
        JSON.stringify(response.data),
        "utf8",
        err => {
          if (err) {
            console.log(err);
          }
        }
      );
      this.setState({ videoList: response.data });
      this.getMainVideo();
    });
  }

  componentDidUpdate(prevProp, prevState) {
    if (prevState.mainVideoId !== this.state.mainVideoId) {
      this.getMainVideo();
    }
  }

  getMainVideo = () => {
    axios.get(`${$url}${this.state.mainVideoId}${$key}`).then(response => {
      const mainVideo = response.data;
      this.setState({ mainVideo });
      this.setState({ commentCount: mainVideo.comments.length });
      this.setState({ comments: mainVideo.comments });
    });
  };

  eventHandler = event => {
    event.preventDefault();
    const comments = event.target.commentInput.value;
    if (comments !== "") {
      axios
        .post(`${$url}${this.state.mainVideoId}/comments${$key}`, {
          comment: comments,
          name: "Daniel Kim"
        })
        .then(response => {
          this.getMainVideo();
        });
      event.target.reset();
    } else {
      window.alert("Please type comments");
    }
  };

  // postVideo = event => {
  //   event.preventDefault();
  //   const title = event.target.video-title.value;
  //   const description = event.target.video-desc.value;
  //   if (title !== "" && description !== "") {
  //     axios
  //       .post(`${$url}${this.state.mainVideoId}${$key}`, {
  //         title: title,
  //         channel: "Dainel Kim",
  //         image: "https://i.imgur.com/l2Xfgpl.jpg",
  //         description: descirption,
  //         name: "Daniel Kim",
  //         timestamp: new Date().getTime(),
  //         views: 0,
  //         likes: 0,
  //         comments: [
  //           {
  //             name: "Micheal Lyons",
  //             comment:
  //               "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of acconcert I have EVER witnessed.",
  //             id: "1ab6d9f6-da38-456e-9b09-ab0acd9ce818",
  //             likes: 0,
  //             timestamp: 1545162149000
  //           },
  //           {
  //             name: "Gary Wong",
  //             comment:
  //               "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!",
  //             id: "cc6f173d-9e9d-4501-918d-bc11f15a8e14",
  //             likes: 0,
  //             timestamp: 1544595784046
  //           },
  //           {
  //             name: "Theodore Duncan",
  //             comment:
  //               "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!",
  //             id: "993f950f-df99-48e7-bd1e-d95003cc98f1",
  //             likes: 0,
  //             timestamp: 1542262984046
  //           }
  //         ]
  //       })
  //       .then(response => {
  //         this.getMainVideo();
  //       });
  //     event.target.reset();
  //   } else {
  //     window.alert("Please type both title and description of video");
  //   }
  // };

  render() {
    return (
      <Router>
        <Header />
        <Switch>
          <Route
            path="/upload"
            render={() => {
              return (
                <main>
                  <Upload postVideo={this.postVideo} />
                </main>
              );
            }}
          />
          <Route
            path="/:id"
            render={props => {
              if (props.match.params.id !== this.state.mainVideoId) {
                this.setState({ mainVideoId: props.match.params.id });
              }
              return (
                <main>
                  <div className="mainVideo-container">
                    <Video mainVideo={this.state.mainVideo} />
                    <VideoControls mainVideo={this.state.mainVideo} />
                  </div>
                  <div className="desktop-view">
                    <div className="desktop-view__left">
                      <VideoInfo mainVideo={this.state.mainVideo} />
                      <Conversation
                        commentCount={this.state.commentCount}
                        onSubmit={this.eventHandler}
                      />
                      <Comments
                        comments={this.state.comments}
                        mainVideoId={this.state.mainVideoId}
                        deleteHandler={this.getMainVideo}
                      />
                    </div>
                    <div className="desktop-view__right">
                      <VideoList
                        videoList={this.state.videoList}
                        mainVideoId={this.state.mainVideoId}
                      />
                    </div>
                  </div>
                </main>
              );
            }}
          />
          <Route
            path="/"
            exact
            render={() => {
              if ($homeKey !== this.state.mainVideoId) {
                this.setState({ mainVideoId: $homeKey });
              }
              return (
                <main>
                  <div className="mainVideo-container">
                    <Video mainVideo={this.state.mainVideo} />
                    <VideoControls mainVideo={this.state.mainVideo} />
                  </div>
                  <div className="desktop-view">
                    <div className="desktop-view__left">
                      <VideoInfo mainVideo={this.state.mainVideo} />
                      <Conversation
                        commentCount={this.state.commentCount}
                        onSubmit={this.eventHandler}
                      />
                      <Comments
                        comments={this.state.comments}
                        mainVideoId={this.state.mainVideoId}
                        deleteHandler={this.getMainVideo}
                      />
                    </div>
                    <div className="desktop-view__right">
                      <VideoList
                        videoList={this.state.videoList}
                        mainVideoId={this.state.mainVideoId}
                      />
                    </div>
                  </div>
                </main>
              );
            }}
          />
        </Switch>
      </Router>
    );
  }
}

export default App;
