import React from "react";
import faceImage from "../assets/Images/face.png";

export default function conversation(props) {
  if (
    props.mainVideo.comments === null &&
    props.mainVideo.comments === undefined &&
    props.mainVideo.comments === 0
  ) {
    console.log("data loading");
  } else {
    const commentLength = props.mainVideo.comments.length;
    return (
      <div className="conv">
        <h1 className="conv__title">{`${commentLength} Comments`}</h1>
        <form id="comments" className="conv__join" name="comment-form">
          <div className="conv__join-left">
            <img src={faceImage} alt="user-face" />
          </div>
          <div className="conv__join-right">
            <p className="conv__join-right-title">JOIN THE CONVERSATION</p>
            <input
              className="conv__join-right-input"
              name="input"
              placeholder="your comments"
            ></input>
            <button className="conv__join-right-button">COMMENT</button>
          </div>
        </form>
      </div>
    );
  }
}
