import React from "react";
import faceImage from "../assets/Images/face.png";

export default function comments(props) {
  const commentList = props.comments.map(() => {
    return (
      <div className="comment">
        <div className="comment__left">
          <img src={faceImage} alt="user-face" />
        </div>
        <div className="comment__right">
          <div className="comment__right-top">
            <p className="comment__right-top-name">{props.comments.name}</p>
            <p className="comment__right-top-date">{props.comments.date}</p>
          </div>
          <div className="comment__paragraph">
            <p className="comment__paragraph-text">{props.comments.comment}</p>
          </div>
        </div>
      </div>
    );
  });

  return { commentList };
}
