import React from "react";
import Header from "./components/header";
import Comments from "./components/comments";
import Conversation from "./components/conversation";
import Video from "./components/video";
import VideoInfo from "./components/videoInfo";
import VideoList from "./components/videoList";
import "./styles/main.css";
import VideoControls from "./components/videoControls";
import axios from "axios";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
//https://project-2-api.herokuapp.com/videos?api_key=Daniel
// //comment data
const comments = [
  {
    name: "Micheal Lyons",
    timestamp: "1545120000000",
    comment:
      "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of a concert I have EVER witnessed.",
    img: "/Images/face0.png"
  },
  {
    name: "Gary Wong",
    timestamp: "1545120000000",
    comment:
      "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!",
    img: "/Images/face1.png"
  },
  {
    name: "Theodore Duncan",
    timestamp: "1542268800000",
    comment:
      "How can someone be so good!!! You can tell he lives for this and loves to do it every day. Everytime I see him I feel instantly happy! He’s definitely my favorite ever!",
    img: "/Images/face2.png"
  }
];

// Side Video Object
const sideVideos = [
  {
    id: "video1",
    title: "Become A Travel Pro In One Easy Lesson",
    channel: "Todd Welch",
    image: "/Images/video-list-1.jpg"
  },
  {
    id: "video2",
    title: "Les Houches The Hidden Gem Of The Chamonix",
    channel: "Cornelia Blair",
    image: "/Images/video-list-2.jpg"
  },
  {
    id: "video3",
    title: "Travel Health Useful Medical Information For",
    channel: "Glen Harper",
    image: "/Images/video-list-3.jpg"
  },
  {
    id: "video4",
    title: "Cheap Airline Tickets Great Ways To Save",
    channel: "Emily Harper",
    image: "/Images/video-list-4.jpg"
  },
  {
    id: "video5",
    title: "Take A Romantic Break In A Boutique Hotel",
    channel: "Ethan Owen",
    image: "/Images/video-list-5.jpg"
  },
  {
    id: "video6",
    title: "Choose The Perfect Accommodations",
    channel: "Lydia Perez",
    image: "/Images/video-list-6.jpg"
  },
  {
    id: "video7",
    title: "Cruising Destination Ideas",
    channel: "Timothy Austin",
    image: "/Images/video-list-7.jpg"
  },
  {
    id: "video8",
    title: "Train Travel On Track For Safety",
    channel: "Scotty Cranmer",
    image: "/Images/video-list-8.jpg"
  }
];

//Main Video Object
const mainVideo = {
  id: "id type of string",
  title: "BMX Rampage: 2018 Highlights",
  description:
    "On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title",
  channel: "By Red Cow",
  image: "/Images/video-list-0.jpg",
  views: "1,001,023",
  likes: "110,985",
  duration: "42",
  video: "/Images/video-list-0.jpg",
  timestamp: "1545120000000",
  comments: "[]"
};

class App extends React.Component {
  state = {
    commentCount: comments.length
  };

  clickHandler = event => {
    event.preventDefault();
    /**
     * create a new comment object and put it into the comment array.
     * clear the comment input and update the state.commentNumber
     */
    const newComments = event.target.input.value;
    if (newComments !== "") {
      const newCommentObject = {
        name: "Daniel Kim",
        timestamp: JSON.stringify(new Date().getTime()),
        comment: newComments,
        img: "/Images/face.png"
      };
      comments.unshift(newCommentObject);
      event.target.reset();
      this.setState({ commentCount: comments.length });
    } else {
      window.alert("Please write your comments");
    }
  };

  render() {
    return (
      <Router>
        <Header />
        <Route />
        <main>
          <div className="mainVideo-container">
            <Video mainVideo={mainVideo} />
            <VideoControls mainVideo={mainVideo} />
          </div>
          <div className="desktop-view">
            <div className="desktop-view__left">
              <VideoInfo mainVideo={mainVideo} />
              <Conversation
                onSubmit={this.clickHandler}
                comments={comments}
                count={this.state.commentCount}
              />
              <Comments comments={comments} />
            </div>
            <div className="desktop-view__right">
              <VideoList videoList={sideVideos} />
            </div>
          </div>
        </main>
      </Router>
    );
  }
}

export default App;

/**
 * Functional Requirements
Clicking the BrainFlix Logo must link back to the home page.
There must be 3 routes:
The Home/Video Player Page for displaying the details of a video.
The Video Upload Page.
A route which will load the video with the provided video id to be displayed using the Video Player Page.
Clicking on a video in the “Next Video” list must go to the Video Details Page for the selected video and display all the information for the video such as (likes, views, author, etc).
Clicking on the “Upload” button must link to the Video Upload Page.


        <main>
          <div className="mainVideo-container">
            <Video />
            <VideoControls />
          </div>
          <div className="desktop-view">
            <div className="desktop-view__left">
              <VideoInfo />
              <Conversation />
              <Comments />
            </div>
            <div className="desktop-view__right">
              <VideoList />
            </div>
          </div>
        </main>
