import React from "react";
import VideoControls from "./videoControls";
const $key = "?api_key=Daniel";

export default function video(props) {
  const source = props.mainVideo.video + $key;
  const ref = React.createRef();
  console.log(ref);
  return (
    <>
      <div className="video">
        <video
          src={source}
          ref={ref}
          className="video__playing"
          poster={props.mainVideo.image}
        ></video>
      </div>
      <VideoControls ref={ref} />
    </>
  );
}
